function geoFindMe(t){t.preventDefault();const o=document.querySelector("#status");document.querySelector("#location");navigator.geolocation?(o.textContent="melacak...",navigator.geolocation.getCurrentPosition(function(t){const e=t.coords.longitude,n=t.coords.latitude;o.textContent="",window.location.href=`/products?location=${`[${e}, ${n}]`}`},function(){o.textContent="Tidak dapat melacak lokasi anda"})):o.textContent="Geolocation tidak mendukung untuk browser anda"}document.querySelector("#find-me").addEventListener("click",geoFindMe);